const db = require('./db');
(async () => {
    var dataBase = {
        verifyUser: async function (username, password) {
            return new Promise(async function (resolve, reject) {
                let q = `SELECT * FROM _user WHERE username = '${username}' AND _user.passwrd = '${password}';`
                let rows = await db.query(
                    q
                )
                let resultUserName = null;
                let resultFirstName = null;
                let resultLastName = null;
                if (rows.rows[0] === undefined) {
                    reject(new Error("user doesn't exist"))
                } else {
                    resultUserName = rows.rows[0].username;
                    resultFirstName = rows.rows[0].ime;
                    resultLastName = rows.rows[0].prezime;
                    resolve({
                        username: resultUserName,
                        firstName: resultFirstName,
                        lastName: resultLastName,
                    })
                }

            });
        },
        getRoles: async function (userInfo) {
            return new Promise(async function (resolve, reject) {
                let q = `SELECT * FROM _role WHERE username = '${userInfo.username}';`
                let rows = await db.query(
                    q
                )
                if (rows.rows[0] === undefined) {
                    reject(new Error("user has no roles"))
                } else {
                    let result = []
                    for (const row of rows.rows) {
                        result.push(row.userrole)
                    }
                    resolve({ roles: result })
                }
            });
        },
        logAccess: async function (userInfo) {
            return new Promise(async function (resolve, reject) {
                try {
                    let date = new Date().toISOString().slice(0, 19).replace('T', ' '); // s stack overflowa
                    let q = `INSERT INTO _log values ('${date}','${userInfo.username}');`
                    //console.log(q)
                    let rows = await db.query(q)
                    resolve("logged successfully");
                }
                catch (e) {
                    reject("logged unsuccessfull")
                }
            });
        }
    };
    //user verification
    const verifyUser = async function (username, password) {
        try {
            const userInfo = await dataBase.verifyUser(username, password);
            const rolesInfo = await dataBase.getRoles(userInfo);
            const logStatus = await dataBase.logAccess(userInfo);
            return true;
        } catch (e) {
            console.log(`error: ${e.message}`)
            return false
        }
    };

    const userInfo = await verifyUser("username", "passwrd");
    console.log(`user valid: ${userInfo}`)

})();
```

