        /// <summary>
        /// Provjera greške zaokruživanja kod unarnih operacija.
        /// </summary>
        [TestMethod]
        public void CheckDisplay_UnaryOperation_TooLargeResult()
        {
            calculator = Factory.CreateCalculator();
            calculator.Press('2');
            calculator.Press('5');
            calculator.Press('4');
            calculator.Press('6');
            calculator.Press('5');
            calculator.Press('5');
            calculator.Press('Q');
            string displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("-E-", displayState);
            // Dobiveni rezultat ima više od 10 znamenki prije decimalnog znaka
        }

        /// <summary>
        /// Divlja provjera.
        /// </summary>
        [TestMethod]
        public void CheckDisplay_CrazyOperation()
        {
            calculator = Factory.CreateCalculator();
            calculator.Press('2');
            calculator.Press('+');
            calculator.Press('I');
            // Ovaj slučaj je već pokriven (ispisuje se 0.5, ali pri izračunu se pamti 2)
            string displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("0,5", displayState);

            calculator.Press('5');
            calculator.Press('6');
            calculator.Press('Q');
            calculator.Press('P');
            // Kvadriranje broja 56, spremanje rezultata u memoriju te potom čišćenje display-a
            calculator.Press('C');
            calculator.Press('3');

            calculator.Press('-');
            calculator.Press('2');
            // Uzastopno mijenjanje predznaka
            calculator.Press('M');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("-2", displayState);
            calculator.Press('M');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("2", displayState);
            calculator.Press('Q');

            calculator.Press('*');
            calculator.Press('2');
            calculator.Press('=');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("2", displayState);
            // Primjer iz docx-a: 2 + 3 - 2^2 * 2 = 2

            // Dohvat memorije (gornji 'C' je obrisao samo ekran, nije dirao memoriju)
            calculator.Press('G');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("3136", displayState);

            // Kvadriranje dohvaćenog broja
            calculator.Press('Q');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("9834496", displayState);

            // Operacija s dobivenim rezultatom
            calculator.Press('*');
            // Unos decimalne točke odmah nakon binarnog operatora
            calculator.Press(',');
            calculator.Press('7');
            calculator.Press('S');
            calculator.Press('=');
            // Decimalni broj rezultata je '266', nije '268'
            // '268' bi bilo da se sinus ne zaokružuje na 10 decimala
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("6335556,266", displayState);
        }

        /// <summary>
        /// NAPOMENA: Ovo nije definirano u danom docx-u tako da se MOŽDA uopće ni ne treba izvoditi
        /// NAPOMENA: Ovo mi je bilo čisto logično da radi
        /// Provjera operacija s istim brojem (2*= --> 2*2)
        /// Provjera operacija s istim brojem (2-= --> 2-2)
        /// Provjera operacija s istim brojem (2/= --> 2/2)
        /// </summary>
        [TestMethod]
        public void CheckDisplay_PressEqualAfterAny()
        {
            // Naravno, ovo nema uopće smisla (zašto bi itko itko oduzimao broj sa samim sobom? al ajde).
            calculator = Factory.CreateCalculator();
            calculator.Press('2');
            calculator.Press('-');
            calculator.Press('=');
            string displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("0", displayState);

            // Također nema smisla dijelit broj sa samim sobom, al et.
            calculator = Factory.CreateCalculator();
            calculator.Press('2');
            calculator.Press('/');
            calculator.Press('=');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("1", displayState);

            // Ovo je jedino što mi zapravo ima smisla (mada će netko reći da ovo dođe kao Q).
            calculator = Factory.CreateCalculator();
            calculator.Press('2');
            calculator.Press('*');
            calculator.Press('=');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("4", displayState);
        }

        /// <summary>
        /// Provjera promjena predznaka
        /// </summary>
        [TestMethod]
        public void CheckDisplay_SignChange_Stationary()
        {
            // Ako nema unesenog broja tipka promjene predznaka ne radi ništa
            calculator = Factory.CreateCalculator();
            calculator.Press('M');
            calculator.Press('M');
            calculator.Press('M');
            string displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("0", displayState);

            // Tehnički pisanje samo zareza nije još nikakav broj - jelda? Ispravite me ako sam u krivu
            calculator.Press(',');
            calculator.Press('M');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("0,", displayState);
            // Tek kad se unese neki broj onda ima smisla stavit minus? eh?
            calculator.Press('2');
            calculator.Press('M');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("-0,2", displayState);
        }

        /// <summary>
        /// Provjera rada memorije
        /// </summary>
        [TestMethod]
        public void CheckDisplay_MemoryCheck()
        {
            // Vodeći se prethodnom dilemom u vezi postojanja broja ovo bi trebalo bacit grešku jer još nema 
