            calculator = Factory.CreateCalculator();
            calculator.Press(',');
            calculator.Press('P');
            string displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("-E-", displayState);
            // Provjera rada memorije s decimalnim brojevima
            calculator.Press('2');
            calculator.Press(',');
            calculator.Press('2');
            calculator.Press('P');
            calculator.Press('2');
            calculator.Press('P');
            calculator.Press('G');
            displayState = calculator.GetCurrentDisplayState();
            Assert.AreEqual("2,22", displayState);
        }

