db.dvdrent.mapReduce(function() {
    const date = new Date(this.rental_date);
    const year = date.getFullYear();
    if (year >= 2009 && year <= 2015) {
        if (year != 2015) {
            emit({
                year: year + 1,
            }, {
                br: 0,
                yearBefore: 1,
            });
        }
        if (year != 2009) {
            emit({
                year: year,
            }, {
                br: 1,
                yearBefore: 0,
            });
        }
    }
}, function(key, values) {
    const rv = {
        br: 0,
        yearBefore: 0,
    };
    values.forEach((value) => {
        rv.br += value.br;
        rv.yearBefore += value.yearBefore;
    });
    return rv;
}, {
    finalize: function(key, value) {
        let diff = value.br / value.yearBefore - 1;
        diff = diff.toFixed(3) + '%';
        return {
            br: value.br,
            diff: diff,
        };
    },
    out: {
        inline: 1,
    },
});
```

