`%% Zadani podatci

H = 409;

mean = 400;

dev = 100;

TEC_S1 = 40;

TEC_S2 = 42;

TEC_S3 = 34;

TEC_S4 = 39;

dx = 293;

dy = 291;

x_min = 1185;

x_max = 1580;

y_min = 4995;

y_max = 5550;

%% a)

gd = makedist('Normal', 'mu', mean, 'sigma', dev);

