fprintf('Postotak ukupnog sadr≈æaja elektrona u ionosferi iznad ISS2 je %.4f %%\n', postotak)

%% b)

x_p = x_min + dx;

y_p = y_min + dy;

fprintf('Koordinate toƒçke p su (%i, %i)\n', x_p, y_p)

%% c)

x_pp = (x_p - x_min)/(x_max - x_min);

y_pp = (y_p - y_min)/(y_max - y_min);

TEC_P = TEC_S1*x_pp*y_pp + TEC_S2*(1-x_pp)*y_pp + TEC_S3*(1-x_pp)*(1-y_pp) + TEC_S4*x_pp*(1-y_pp);

fprintf('Ukupni sadr≈æaj elektrona u ionosferi za lokaciju P je %.4f TECU\n', TEC_P)

%% d)

f = 1575.42e+06;

