variable count := std_logic_vector (15 downto 0);
variable ulaz := std_logic_vector (3 downto 0);
ulaz := dodaj;
begin
process(RESET, CLK, ENABLE, UP_DOWN)
begin
if RESET = ‘1’ then
count := ‘0000 0000 0000 0000’;
end if;
if CLK’event and CLK=’1’ then
if ENABLE = ‘1’:
if UP_DOWN = ‘1’ then
count = count + ulaz;
elsif UP_DOWN = ‘0’ then
count = count – ulaz;
end if;
end if;
end if;
brojac <= count
end process;
end ARHITEKTURA;
